    using Cast for bytes32;
    using Cast for uint256;
    using Cast for sIERC20;
    using Clones for address;
    using ERC165Checker for address;

    bytes32 public constant FRACTIONALIZE_ROLE = keccak256("FRACTIONALIZE_ROLE");
    bytes32 public constant DERRIDA = 0x1d2496c631fd6d8be20fb18c5c1fa9499e1f28016c62da960ec6dcf752f2f7ce; // keccak256("Le spectral, ce sont ces autres, jamais prsents comme tels, ni vivants ni morts, avec lesquels je m'entretiens");

