        require(data.length == 192, "Vault: invalid fractionalization data length");

        bytes memory _data = data; // one cannot mload data located in calldata
        bytes32 name;
        bytes32 symbol;
        uint256 cap;
        address admin;
        address broker;
        bytes32 derrida;

